import React, { useEffect, useState } from 'react';

export default function GoogleAuth () {

    const [isSignedIn, setSignedIn] = useState (null)

    const authMethod = window.gapi.auth2.getAuthInstance();

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({ 
                clientId: '1068486064245-b6gc1pjkgm8bjeqpe7324d44g9de3j8n.apps.googleusercontent.com',
                scope: 'email' 
            }).then(() => {
                const auth = window.gapi.auth2.getAuthInstance();
                setSignedIn(auth.isSignedIn.get())

                auth.isSignedIn.listen(onAuthChange)
            });
        });
     });

    function onAuthChange () {
        setSignedIn(authMethod.isSignedIn.get())
    }

    function onSignIn () {
        return authMethod.signIn();
    }

    function onSignOut () {
        return authMethod.signOut();
    }

    function renderAuthButton () {
        if (isSignedIn === null){
            return null
        } else if (isSignedIn) {
            return (
                <button onClick={onSignIn} className="ui red google button">
                    <i className="google icon"></i>
                    Sign Out
                </button>)
        } else {
            return (
                <button onClick={onSignOut} className="ui red google button">
                    <i className="google icon"></i>
                    Sign In with Google
                </button>)
        }
    }
 
    return <div>{renderAuthButton()}</div>

}
