import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import Modal from '../Modal'
import history from '../../history'
import { fetchStream, deleteStream } from '../../actions'

function StreamShow (props) {
    
    useEffect(() => {
        props.fetchStream(props.match.params.id)
    }, [])

    const id = props.match.params.id

    const actions = (
        
        <React.Fragment>
            <button onClick={() => props.deleteStream(id)} className="ui button negative">Delete</button>
            <Link to={'/'} className="ui button">Cancel</Link>
        </React.Fragment>
    )


    function renderContent () {
        if (!props.stream) {
            return <div>Loading...</div>
        }

        return (
            <div>
                <h1>{props.stream.title}</h1>
                <h5>{props.stream.description}</h5>
            </div>
        )
    }

    return renderContent()
}

const mapStateToProps = (state, ownProps) => {
    return {
        stream: state.streams[ownProps.match.params.id]
    }
}

export default connect (mapStateToProps, { fetchStream, deleteStream })(StreamShow)
