import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import history from '../history'
import { fetchStream } from '../actions'

function BreadCrumb (props) {

    useEffect(() => {
        props.fetchStream(props.match.params.id)
    }, [])

    const crumbs = []

    function renderCrumbs() {
        let breadCrumb, url = ''

        url = history.location.pathname

        breadCrumb = url !== '/' ? <Link to={`/`} className="section">Home</Link> : <div class="active section">Home</div>
        
        crumbs.push(breadCrumb)

        url = url.split('/')
 
        url.map(location => {
            
            if (location === 'streams') {
                crumbs.push(
                    <React.Fragment>
                             <div class="divider"> / </div>
                             <div class="section"> Streams</div>
                    </React.Fragment>)
            }

            if (location === 'show'){

                crumbs.push(
                    <React.Fragment>
                            <div class="divider"> / </div>
                            <div class="section"> Show</div>
                    </React.Fragment>)
            }
        }) 

        return crumbs;
    }

    return (
        <div class="ui breadcrumb">
            {renderCrumbs()}
        </div>
    )
}

const mapStateToProps = (state, ownProps) => {
    return {
        stream: state.streams[ownProps.match.params.id]
    }
}

export default connect (mapStateToProps, {fetchStream})(BreadCrumb)
