import React, { useEffect, useState } from 'react';

export default function GoogleAuth () {

    const [isSignedIn, setSignedIn] = useState (null)

    useEffect(() => {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({ 
                clientId: '1068486064245-b6gc1pjkgm8bjeqpe7324d44g9de3j8n.apps.googleusercontent.com',
                scope: 'email' 
            }).then(() => {
                const auth = window.gapi.auth2.getAuthInstance();
                setSignedIn(auth.isSignedIn.get())

                auth.isSignedIn.listen(onAuthChange())
            });
        });
     });

    function onAuthChange () {
        let status = window.gapi.auth2.getAuthInstance();
        setSignedIn(status.isSignedIn.get())
    }

    function renderAuthButton () {
        if (isSignedIn === null){
            return <div>Don't know if signed In</div>
        } else if (isSignedIn) {
            return <div>I am signed in!</div>
        } else {
            return <div>Not signed in</div>
        }
    }
 
    return <div>{renderAuthButton()}</div>

}
